import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Add = () => {
    const [book, setBook] = useState({
        title: "",
        desc: "",
        price: null,
        coverFile: null,
        coverURL: null // New state to store the URL of the selected image
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        setBook({ ...book, [e.target.name]: e.target.value });
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setBook({ ...book, coverFile: file, coverURL: URL.createObjectURL(file) }); // Create object URL
    };

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('title', book.title);
            formData.append('desc', book.desc);
            formData.append('price', book.price);
            formData.append('cover', book.coverFile);
            await axios.post("http://10.0.0.181:8800/books", formData);
            navigate("/");
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <div className='form'>
            <h1>Add New Book</h1>
            <input type="text" placeholder='title' onChange={handleChange} name="title" />
            <input type="text" placeholder='description' onChange={handleChange} name="desc" />
            <input type="number" placeholder='price' onChange={handleChange} name="price" />
            <input type="file" onChange={handleFileChange} name="coverFile" accept=".jpeg, .png, .jpg" />
            {book.coverURL && <img src={book.coverURL} alt="Selected Cover" />} {/* Display selected image */}
            <button className="formButton" onClick={handleClick}>Add</button>
        </div>
    );
};

export default Add;
