import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Add = () => {
    const [book, setBook] = useState({
        title: "",
        desc: "",
        price: "",
        coverFile: null,
        cover: "" // Added cover state for the Base64 representation of the image
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        setBook(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Convert image file to Base64
            const coverBase64 = await convertToBase64(book.coverFile);

            // Update the book state with the Base64 representation of the image
            setBook(prev => ({ ...prev, cover: coverBase64 }));

            // Send the book data to the backend
            await axios.post("http://10.0.0.181:8800/books", book);

            navigate("/"); // Navigate to home page after successful submission
        } catch (err) {
            console.log(err);
        }
    };

    const handleFileChange = (e) => {
        setBook(prev => ({ ...prev, coverFile: e.target.files[0] }));
    };

    // Function to convert image file to Base64
    const convertToBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => resolve(reader.result);
            reader.onerror = error => reject(error);
        });
    };

    return (
        <div className='form'>
            <h1>Add New Book</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder='Title' onChange={handleChange} name="title" />

                <input type="text" placeholder='Description' onChange={handleChange} name="desc" />

                <input type="number" placeholder='Price' onChange={handleChange} name="price" />

                <input type="file" onChange={handleFileChange} name="cover" accept=".jpeg, .png, .jpg" />

                <button className="formButton" type="submit">Add</button>
            </form>
        </div>
    );
}

export default Add;
