import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Add = () => {
    const [book, setBook] = useState({
        title: "",
        desc: "",
        price: null,
        coverFile: null,
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        setBook({ ...book, [e.target.name]: e.target.value });
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setBook({ ...book, coverFile: file });
    };

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const base64 = await convertToBase64(book.coverFile);
            const formData = {
                title: book.title,
                desc: book.desc,
                price: book.price,
                cover: base64,
            };
            await axios.post("http://10.0.0.181:8800/books", formData);
            navigate("/");
        } catch (err) {
            console.log(err);
        }
    };

    const convertToBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => {
                resolve(reader.result.split(',')[1]); // Remove the data URL prefix
            };
            reader.onerror = (error) => {
                reject(error);
            };
        });
    };

    return (
        <div className='form'>
            <h1>Add New Book</h1>
            <input type="text" placeholder='title' onChange={handleChange} name="title" />
            <input type="text" placeholder='description' onChange={handleChange} name="desc" />
            <input type="number" placeholder='price' onChange={handleChange} name="price" />
            <input type="file" onChange={handleFileChange} accept=".jpeg, .png, .jpg" />
            <button className="formButton" onClick={handleClick}>Add</button>
        </div>
    );
};

export default Add;
