import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Add = () => {
    const [book, setBook] = useState({
        title: '',
        desc: '',
        price: '',
        coverFile: null, // Hold the file object here
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        setBook({ ...book, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const formData = new FormData();
            formData.append('title', book.title);
            formData.append('desc', book.desc);
            formData.append('price', book.price);
            formData.append('cover', book.coverFile); // Append the image file directly

            await axios.post("http://10.0.0.181:8800/books", formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            navigate("/");
        } catch (error) {
            console.log(error);
        }
    };

    const handleFileUpload = (e) => {
        const file = e.target.files[0];
        setBook({ ...book, coverFile: file });
    };

    return (
        <div className='form'>
            <h1>Add New Book</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder='title'
                    onChange={handleChange}
                    name="title"
                    value={book.title}
                />
                <input
                    type="text"
                    placeholder='description'
                    onChange={handleChange}
                    name="desc"
                    value={book.desc}
                />
                <input
                    type="number"
                    placeholder='price'
                    onChange={handleChange}
                    name="price"
                    value={book.price}
                />
                <input
                    type="file"
                    placeholder='cover'
                    onChange={handleFileUpload}
                    name="cover"
                />
                <button className="formButton" type="submit">Add</button>
            </form>
        </div>
    );
};

export default Add;
